    private void jMenuItem6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem6ActionPerformed
        // TODO add your handling code here:
        int valor = 0;
        
        valor = Integer.parseInt(JOptionPane.showInputDialog(this, "Ingrese un numero en la pila"));
        pila.push(valor);
    }//GEN-LAST:event_jMenuItem6ActionPerformed

    private void jMenuItem3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem3ActionPerformed
        // TODO add your handling code here:
        int valor;
        
        valor = JOptionPane.showConfirmDialog(this, "Se eliminara el ultimo dato ingresado",
                "Esta seguro de hacer pop en la lista?",                  
                JOptionPane.YES_OPTION);
        
        if(pila.estaVacio()) JOptionPane.showMessageDialog(this, 
                "Asegurece de que la pila tenga datos", "La pila esta vacia",
                JOptionPane.WARNING_MESSAGE);
        else{
            if(valor == 0){
                pila.pop();
                JOptionPane.showMessageDialog(this, "El dato se ha eliminado con exito");
            }else{
                JOptionPane.showMessageDialog(this, "Se ha cancelado la eliminacion");
            }
        }
    }//GEN-LAST:event_jMenuItem3ActionPerformed

