    private void jMenuItem5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem5ActionPerformed
        // TODO add your handling code here:
        int valor;
        
        valor = JOptionPane.showConfirmDialog(this, "Se eliminara el primer dato ingresado",
                "Esta seguro de desencolar en la lista?",                  
                JOptionPane.YES_OPTION);
        
        if(cola.estaVacio()) JOptionPane.showMessageDialog(this, 
                "Asegurece de que la cola tenga datos", "La cola esta vacia",
                JOptionPane.WARNING_MESSAGE);
        else{
            if(valor == 0){
                cola.desencolar();
                JOptionPane.showMessageDialog(this, "El dato se ha eliminado con exito");
            }else{
                JOptionPane.showMessageDialog(this, "Se ha cancelado la eliminacion");
            }
        }
    }//GEN-LAST:event_jMenuItem5ActionPerformed

